function initalslick(){$(".regular").slick({dots:!0,infinite:!0,slidesToShow:4,slidesToScroll:1,responsive:[{breakpoint:1024,settings:{slidesToShow:4,slidesToScroll:1,infinite:!0,dots:!0}},{breakpoint:1023,settings:{slidesToShow:3,slidesToScroll:1,infinite:!0,dots:!0}},{breakpoint:768,settings:{slidesToShow:1,slidesToScroll:1,infinite:!0,dots:!1}}]})}function makeAjaxCall(i,e,s){return $.ajax({url:i,method:e,dataType:"json"})}var URL="https://gist.githubusercontent.com/roblos12/151417b9d5bcd444a2351b6d58db3fdc/raw/acc7ed077a9eb0cba0651bf9759d7b58cacc83c3/data.json";makeAjaxCall(URL,"GET").then(function(e){console.log(e.numberOfItems);var s="";for(i=0;i<e.numberOfItems;i++)s+="<div class='productslider'><div class='product-tile-inner'><div class='product-tile'>",s+="<div class='product-image'>",s+="<a href='"+e.carouselData[i].url+"'><img src='"+e.carouselData[i].productImageUrl+"' alt='"+e.carouselData[i].productImageAltText+"' title='"+e.carouselData[i].productImageAltText+"' style='width:100%'></a>",s+="</div>",s+="<div class='product-name'>"+e.carouselData[i].name+"</div>",s+="<div class='product-price'>"+e.carouselData[i].salesUnit+"</div><div class='product-price'>"+e.carouselData[i].price.currency+"</div><div class='product-price'>"+e.carouselData[i].price.formattedValue+"</div>",s+="</div></div></div>";$(".regular").append(s),initalslick()},function(i){console.log("error in processing your request",i)});